mapper.select:
SELECT a.appId,a.appName,a.packageName,u.nickName,a.downloadlink,a.appStatus,a.platform,a.terminal,DATE_FORMAT(a.createTime,"%Y-%m-%d %H:%i:%s")as createTime
from a_app a,u_user u
where a.userId = u.userId

    and u.loginName = #{loginName}


    and u.nickName = #{nickName}


    and a.appId = #{appId}


    and a.appName = #{appName}


    and a.appStatus = #{appStatus}


    and u.parentId = #{currentUserId}


    and a.userId = #{currentUserId}

order by a.createTime desc
limit #{num},#{pageSize}

select count(0)
from a_app a,u_user u
where a.userId = u.userId

    and u.loginName = #{loginName}


    and u.nickName = #{nickName}


    and a.appId = #{appId}


    and a.appName = #{appName}


    and a.appStatus = #{appStatus}


    and u.parentId = #{currentUserId}


    and a.userId = #{currentUserId}

select userId
from a_app
where appId = #{appId}

select count(0)
from a_adspace
where appId = #{appId} and spaceType = #{spaceType}

select count(0)
from a_adspace
where spaceName = #{spaceName}

select x.*,IFNULL(y.downstreamRequest,0) as request from
(
select a.appName,a.appId,s.spaceId,s.spaceName,s.spaceType,s.width,s.height,DATE_FORMAT(s.createTime,"%Y-%m-%d %H:%i:%s") as createTime,s.dividedY,s.dividedZ,u.nickName
from a_adspace s,a_app a,u_user u
where s.appId = a.appId and a.userId = u.userId

    and u.loginName = #{loginName}


    and u.nickName = #{nickName}


    and a.appId = #{appId}


    and s.spaceId = #{spaceId}


    and s.spaceType = #{spaceType}


    and u.parentId = #{userId}


    and a.userId = #{userId}

) x
LEFT JOIN a_report_downstream y
on x.spaceId = y.slotId and TO_DAYS(y.createTime) = TO_DAYS(NOW())
order by x.createTime desc
limit #{num},#{pageSize}

select count(0)
from a_adspace s,a_app a,u_user u
where s.appId = a.appId and a.userId = u.userId

    and u.loginName = #{loginName}


    and u.nickName = #{nickName}


    and a.appId = #{appId}


    and s.spaceId = #{spaceId}


    and s.spaceType = #{spaceType}


    and u.parentId = #{userId}


    and a.userId = #{userId}

select count(0)
from a_upstream
where upstreamId = #{upstreamId}

select count(0)
from a_upstream
where spaceId = #{spaceId} and upstreamType = #{upstreamType}

select count(0)
from a_upstream
where spaceId = #{spaceId}

