mapper.select:
select a.appName,a.appId,s.spaceId,d.spaceName,t.upstreamAppId,t.upstreamId,DATE_FORMAT(s.createTime,"%Y-%m-%d") as createTime,s.clickNum,s.clickProbability,s.ecmp,s.income,s.lookPV,t.upstreamType,ut.`name`,s.status,s.statisticsId
from a_statistics s,a_upstream t,a_adspace d,a_app a,u_user u,a_upstream_type ut
where s.upstreamId = t.upstreamId and t.spaceId = d.spaceId and s.appId = a.appId and a.userId = u.userId and t.upstreamType = ut.type

    and a.appName = #{appName}


    and d.spaceName = #{spaceName}


    and u.parentId = #{parentId}


    and a.userId = #{parentId}


    and TO_DAYS(s.createTime) >= TO_DAYS(#{startTime})


    and TO_DAYS(#{endTime}) >= TO_DAYS(s.createTime)


    and s.status = #{status}

order by s.status asc,s.createTime desc
limit #{num},#{pageSize}

SELECT count(0)
from a_statistics s,a_upstream t,a_adspace d,a_app a,u_user u,a_upstream_type ut
where s.upstreamId = t.upstreamId and t.spaceId = d.spaceId and s.appId = a.appId and a.userId = u.userId and t.upstreamType = ut.type

    and a.appName = #{appName}


    and d.spaceName = #{spaceName}


    and u.parentId = #{parentId}


    and a.userId = #{parentId}


    and TO_DAYS(s.createTime) >= TO_DAYS(#{startTime})


    and TO_DAYS(#{endTime}) >= TO_DAYS(s.createTime)


    and s.status = #{status}

select a.appName,a.appId,s.spaceId,d.spaceName,DATE_FORMAT(s.createTime,"%Y-%m-%d") as createTime,SUM(s.clickNum) as clickNum,SUM(s.income) as income,SUM(s.lookPV) as lookPV
from a_statistics s,a_upstream t,a_adspace d,a_app a
where s.upstreamId = t.upstreamId and t.spaceId = d.spaceId and s.appId = a.appId and s.status = 1

    and a.userId = #{parentId}


    and a.appName = #{appName}


    and d.spaceName = #{spaceName}


    and TO_DAYS(s.createTime) >= TO_DAYS(#{startTime})


    and TO_DAYS(#{endTime}) >= TO_DAYS(s.createTime)

GROUP BY s.spaceId,s.createTime
order by s.createTime desc,d.spaceId asc
limit #{num},#{pageSize}

select count(0) from
(SELECT count(0)
from a_statistics s,a_upstream t,a_adspace d,a_app a
where s.upstreamId = t.upstreamId and t.spaceId = d.spaceId and s.appId = a.appId and s.status = 1

    and a.userId = #{parentId}


    and a.appName = #{appName}


    and d.spaceName = #{spaceName}


    and TO_DAYS(s.createTime) >= TO_DAYS(#{startTime})


    and TO_DAYS(#{endTime}) >= TO_DAYS(s.createTime)

GROUP BY s.spaceId,s.createTime) c

select a.probability,a.upstreamType,u.`name`,u.shortName
from a_upstream a,a_upstream_type u
where a.upstreamType = u.type and a.spaceId = #{spaceId}

