mapper.select:
select a.upstreamId,a.upstreamAppId,a.name,p.appId,d.spaceId,d.spaceName,p.appName from
(
    SELECT u.upstreamId,u.upstreamAppId,t.name,u.spaceId
    FROM a_upstream u,a_upstream_type t
    where u.upstreamType = t.type

        and u.upstreamType = #{type}


        and u.upstreamId = #{upstreamId}

) a
LEFT JOIN a_adspace d on a.spaceId = d.spaceId
LEFT JOIN a_app p on d.appId = p.appId
limit #{num},#{pageSize}

select count(0) from
(
SELECT u.upstreamId,u.upstreamAppId,t.name,u.spaceId
FROM a_upstream u,a_upstream_type t
where u.upstreamType = t.type

    and u.upstreamType = #{type}


    and u.upstreamId = #{upstreamId}

) a
LEFT JOIN a_adspace d on a.spaceId = d.spaceId
LEFT JOIN a_app p on d.appId = p.appId

SELECT spaceId,upstreamType
FROM `a_upstream`
where upstreamId = #{upstreamId}

mapper.update:
