where type = #{upstreamType}

mapper.select:
SELECT x.appName, x.downstreamReportId, x.createTime, x.downstreamRequest, y.request, y.response, y.look, y.click, x.appId, x.slotId FROM ( SELECT a.appName, d.downstreamReportId, d.createTime, d.downstreamRequest, d.appId, d.slotId FROM a_report_downstream d, a_app a WHERE d.appId = a.appId
and d.appId = #{appId}
and d.slotId = #{slotId}
and TO_DAYS(d.createTime) >= TO_DAYS(#{startTime})
and TO_DAYS(#{endTime}) >= TO_DAYS(d.createTime)
) x LEFT JOIN ( SELECT downstreamReportId, sum( request ) AS request, sum( response ) AS response, sum( look ) AS look, sum( click ) AS click FROM a_report_upstream GROUP BY downstreamReportId, createTime ) y ON x.downstreamReportId = y.downstreamReportId ORDER BY x.createTime DESC limit #{num},#{pageSize}

SELECT count( 0 ) FROM ( SELECT a.appName, d.downstreamReportId, d.createTime, d.downstreamRequest, d.appId, d.slotId FROM a_report_downstream d, a_app a WHERE d.appId = a.appId
and d.appId = #{appId}
and d.slotId = #{slotId}
and TO_DAYS(d.createTime) >= TO_DAYS(#{startTime})
and TO_DAYS(#{endTime}) >= TO_DAYS(d.createTime)
) x LEFT JOIN ( SELECT downstreamReportId, sum( request ) AS request, sum( response ) AS response, sum( look ) AS look, sum( click ) AS click FROM a_report_upstream GROUP BY downstreamReportId, createTime ) y ON x.downstreamReportId = y.downstreamReportId

SELECT a.appName,a.appId,r.slotId,DATE_FORMAT(r.createTime,'%Y-%m-%d') as createTime,r.downStreamRequest, sum(r.request) as request,sum(r.response) as response,sum(r.look) as look,sum(r.click) as click,count(r.upstreamType) as upstreamNum FROM a_report r,a_app a where r.appId = a.appId
and r.appId = #{appId}
and r.slotId = #{slotId}
and TO_DAYS(r.createTime) >= TO_DAYS(#{startTime})
and TO_DAYS(#{endTime}) >= TO_DAYS(r.createTime)
GROUP BY r.slotId,r.createTime order by r.createTime desc,a.appId asc limit #{num},#{pageSize}

select count(0) from (SELECT a.appName FROM a_report r,a_app a where r.appId = a.appId
and r.appId = #{appId}
and r.slotId = #{slotId}
and TO_DAYS(r.createTime) >= TO_DAYS(#{startTime})
and TO_DAYS(#{endTime}) >= TO_DAYS(r.createTime)
GROUP BY r.slotId,r.createTime) x

