mapper.select:
SELECT a.appId,a.appName,a.packageName,u.nickName,a.downloadlink,a.appStatus,a.platform,a.terminal,DATE_FORMAT(a.createTime,"%Y-%m-%d %H:%i:%s")as createTime
from a_app a,u_user u
where a.userId = u.userId

    and a.appId = #{appId}


    and a.appName = #{appName}


    and a.appStatus = #{appStatus}


    and u.parentId = #{userId}


    and a.userId = #{userId}

order by a.appId asc
limit #{num},#{pageSize}

select count(0)
from a_app a,u_user u
where a.userId = u.userId

    and a.appId = #{appId}


    and a.appName = #{appName}


    and a.appStatus = #{appStatus}


    and u.parentId = #{userId}


    and a.userId = #{userId}

select userId
from a_app
where appId = #{appId}

select count(0)
from a_adspace
where appId = #{appId} and spaceType = #{spaceType}

select count(0)
from a_adspace
where spaceName = #{spaceName}

select a.appName,a.appId,s.spaceId,s.spaceName,s.spaceType,s.width,s.height,DATE_FORMAT(s.createTime,"%Y-%m-%d %H:%i:%s") as createTime
from a_adspace s,a_app a,u_user u
where s.appId = a.appId and a.userId = u.userId

    and a.appName = #{appName}


    and s.spaceName = #{spaceName}


    and s.spaceType = #{spaceType}


    and u.parentId = #{userId}


    and a.userId = #{userId}

order by s.spaceId asc
limit #{num},#{pageSize}

select count(0)
from a_adspace s,a_app a,u_user u
where s.appId = a.appId and a.userId = u.userId

    and a.appName = #{appName}


    and s.spaceName = #{spaceName}


    and s.spaceType = #{spaceType}


    and u.parentId = #{userId}


    and a.userId = #{userId}

select count(0)
from a_upstream
where upstreamId = #{upstreamId}

select count(0)
from a_upstream
where spaceId = #{spaceId} and upstreamType = #{upstreamType}

select count(0)
from a_assign
where spaceId = #{spaceId}

select a.appName,a.appId,s.spaceId,d.spaceName,t.upstreamAppId,t.upstreamId,DATE_FORMAT(s.createTime,"%Y-%m-%d") as createTime,s.clickNum,s.clickProbability,s.ecmp,s.income,s.lookPV,t.upstreamType,ut.`name`
from a_statistics s,a_upstream t,a_adspace d,a_app a,u_user u,a_upstream_type ut
where s.upstreamId = t.upstreamId and t.spaceId = d.spaceId and s.appId = a.appId and a.userId = u.userId and t.upstreamType = ut.type

    and a.appName = #{appName}


    and d.spaceName = #{spaceName}


    and u.parentId = #{parentId}


    and a.userId = #{parentId}


    and TO_DAYS(s.createTime) >= TO_DAYS(#{startTime})


    and TO_DAYS(#{endTime}) >= TO_DAYS(s.createTime)

order by s.createTime desc
limit #{num},#{pageSize}

SELECT count(0)
from a_statistics s,a_upstream t,a_adspace d,a_app a,u_user u,a_upstream_type ut
where s.upstreamId = t.upstreamId and t.spaceId = d.spaceId and s.appId = a.appId and a.userId = u.userId and t.upstreamType = ut.type

    and a.appName = #{appName}


    and d.spaceName = #{spaceName}


    and u.parentId = #{parentId}


    and a.userId = #{parentId}


    and TO_DAYS(s.createTime) >= TO_DAYS(#{startTime})


    and TO_DAYS(#{endTime}) >= TO_DAYS(s.createTime)

select a.appName,a.appId,s.spaceId,d.spaceName,DATE_FORMAT(s.createTime,"%Y-%m-%d") as createTime,SUM(s.clickNum) as clickNum,SUM(s.income) as income,SUM(s.lookPV) as lookPV
from a_statistics s,a_upstream t,a_adspace d,a_app a
where s.upstreamId = t.upstreamId and t.spaceId = d.spaceId and s.appId = a.appId and a.userId = #{parentId}

    and a.appName = #{appName}


    and d.spaceName = #{spaceName}


    and TO_DAYS(s.createTime) >= TO_DAYS(#{startTime})


    and TO_DAYS(#{endTime}) >= TO_DAYS(s.createTime)

GROUP BY s.spaceId,s.createTime
order by s.createTime desc,d.spaceId asc
limit #{num},#{pageSize}

select count(0) from
(SELECT count(0)
from a_statistics s,a_upstream t,a_adspace d,a_app a
where s.upstreamId = t.upstreamId and t.spaceId = d.spaceId and s.appId = a.appId and a.userId = #{parentId}

    and a.appName = #{appName}


    and d.spaceName = #{spaceName}


    and TO_DAYS(s.createTime) >= TO_DAYS(#{startTime})


    and TO_DAYS(#{endTime}) >= TO_DAYS(s.createTime)

GROUP BY s.spaceId,s.createTime) c

select a.*,u.`name`,u.shortName
from a_assign a,a_upstream_type u
where a.upstreamType = u.type and a.spaceId = #{spaceId}

update.foreach:
update a_assign
set probability = #{item.probability}
where spaceId = #{item.spaceId} and upstreamType = #{item.upstreamType}

mapper.select:
select *
from a_upstream_type
limit #{num},#{pageSize}

mapper.update:
update a_upstream_type
set status = #{status}
where id = #{id}

mapper.select:
select a.upstreamId,a.upstreamAppId,a.name,p.appId,d.spaceId,d.spaceName,p.appName from
(
    SELECT u.upstreamId,u.upstreamAppId,t.name,u.spaceId
    FROM a_upstream u,a_upstream_type t
    where u.upstreamType = t.type

        and u.upstreamType = #{type}


        and u.upstreamId = #{upstreamId}

) a
LEFT JOIN a_adspace d on a.spaceId = d.spaceId
LEFT JOIN a_app p on d.appId = p.appId
limit #{num},#{pageSize}

select count(0) from
(
SELECT u.upstreamId,u.upstreamAppId,t.name,u.spaceId
FROM a_upstream u,a_upstream_type t
where u.upstreamType = t.type

    and u.upstreamType = #{type}


    and u.upstreamId = #{upstreamId}

) a
LEFT JOIN a_adspace d on a.spaceId = d.spaceId
LEFT JOIN a_app p on d.appId = p.appId

SELECT spaceId,upstreamType
FROM `a_upstream`
where upstreamId = #{upstreamId}

mapper.update:
update a_upstream
set upstreamId = #{newUpstreamId},upstreamAppId = #{newUpstreamAppId},
upstreamType = #{newUpstreamType},upstreamPackageName = #{newUpstreamPackageName}
where upstreamId = #{upstreamId}

update a_assign
set upstreamType = #{newUpstreamType}
where spaceId = #{spaceId} and upstreamType = #{upstreamType}

mapper.select:
select a.appName,DATE_FORMAT(s.createTime,'%Y-%m-%d') as createTime,s.slotId,s.appId, sum(case s.type when '1' then s.num else 0 end) 'request', sum(case s.type when '2' then s.num else 0 end) 'response', sum(case s.type when '3' then s.num else 0 end) 'look', sum(case s.type when '4' then s.num else 0 end) 'click' from ad_statistics s,a_app a where s.appId = a.appId
and s.appId = #{appId}
and s.slotId = #{slotId}
group by s.slotId,s.createTime ORDER BY s.createTime desc limit #{num},#{pageSize}

select count(0) from (select a.slotId from ad_statistics a where 1 = 1
and a.appId = #{appId}
and a.slotId = #{slotId}
group by a.slotId,a.createTime) x

SELECT a.appName,a.appId,r.slotId,DATE_FORMAT(r.createTime,'%Y-%m-%d') as createTime,r.downStreamRequest, sum(r.request) as request,sum(r.response) as response,sum(r.look) as look,sum(r.click) as click,count(r.upstreamType) as upstreamNum FROM a_report r,a_app a where r.appId = a.appId
and r.appId = #{appId}
and r.slotId = #{slotId}
and TO_DAYS(r.createTime) >= TO_DAYS(#{startTime})
and TO_DAYS(#{endTime}) >= TO_DAYS(r.createTime)
GROUP BY r.slotId,r.createTime order by r.createTime desc,a.appId asc limit #{num},#{pageSize}

select count(0) from (SELECT a.appName FROM a_report r,a_app a where r.appId = a.appId
and r.appId = #{appId}
and r.slotId = #{slotId}
and TO_DAYS(r.createTime) >= TO_DAYS(#{startTime})
and TO_DAYS(#{endTime}) >= TO_DAYS(r.createTime)
GROUP BY r.slotId,r.createTime) x
