mapper.select:
select d.appName,d.appId,d.slotId,d.createTime,d.request,SUM(u.response) as response,SUM(u.look) as look,SUM(u.click) as click from (select a.appName,a.appId,r.slotId,r.createTime,SUM(r.downstreamRequest) as request from a_report_downstream r,a_app a where r.appId = a.appId
and r.appId = #{appId}
and r.slotId = #{slotId}
and TO_DAYS(r.createTime) >= TO_DAYS(#{startTime})
and TO_DAYS(#{endTime}) >= TO_DAYS(r.createTime)
GROUP BY r.slotId,r.createTime ORDER BY r.createTime desc,r.slotId asc limit #{num},#{pageSize}) d LEFT JOIN a_report_upstream u on d.slotId = u.slotId and d.createTime = u.createTime GROUP BY d.slotId,d.createTime ORDER BY d.createTime desc,d.slotId asc limit 0,#{pageSize}

select count(0) from (select r.slotId,r.createTime from a_report_downstream r,a_app a where r.appId = a.appId
and r.appId = #{appId}
and r.slotId = #{slotId}
and TO_DAYS(r.createTime) >= TO_DAYS(#{startTime})
and TO_DAYS(#{endTime}) >= TO_DAYS(r.createTime)
GROUP BY r.slotId,r.createTime) x
